export const notesAndTodos = {
    state: () => ({
       notes: [
           {id: 1, title: 'Products Categories', todos: [
                   {id: 1, todoTitle: 'Make adaptive design', completed: false},
                   {id: 2, todoTitle: 'Test 2', completed: false},
                   {id: 3, todoTitle: 'Test 3', completed: false}
               ]},
           {id: 2, title: 'Burger Menu', todos: [
                   {id: 1, todoTitle: 'aaaaaaaaaaaa', completed: false},
                   {id: 2, todoTitle: 'bbbbbbbbbbbb', completed: false},
                   {id: 3, todoTitle: 'cccccccccccc', completed: false}
               ]}
       ],
        dialogVisibleNote: false,

    }),
    mutations: {
      setNotes(state, notes){
          state.notes = notes
      }
    },
    actions: {
        removeNote({state, commit}, note){
            commit('setNotes', state.notes.filter(n => n.id !== note.id))
        }
    },
    namespaced: true
}
