export const notesAndTodos = {
    state: () => ({
       notes: [
           {id: 1, title: 'Products Categories', todos: [
                   {todoTitle: 'Make adaptive design', id: 1, completed: false},
                   {todoTitle: 'Test 2', id: 2, completed: false},
                   {todoTitle: 'Test 3', id: 3, completed: false}
               ]},
           {id: 2, title: 'Burger Menu', todos: [
                   {todoTitle: 'aaaaaaaaaaaa', id: 1, completed: false},
                   {todoTitle: 'bbbbbbbbbbbb', id: 2, completed: false},
                   {todoTitle: 'cccccccccccc', id: 3, completed: false}
               ]}
       ],
        dialogVisibleNote: false,
        getNotes: {}
    }),
    mutations: {
      setNotes(state, notes){
          state.notes = notes
      },
        setGetNotes(state, getNotes){
          state.getNotes = getNotes
        },
        setDialogVisibleNote(state, dialogVisibleNote){
          state.dialogVisibleNote = dialogVisibleNote
        }
    },
    actions: {
        removeNote({state, commit}, note){
            commit('setGetNotes', note)
            commit('setDialogVisibleNote', true)
        },
        removeNoteConfirm({state, commit}){
            commit('setNotes', state.notes.filter(n => n.id !== state.getNotes.id))
            commit('setDialogVisibleNote', false)
        },
        hideDialogNote({commit}){
            commit('setDialogVisibleNote', false)
        }
    },
    namespaced: true
}
