export const notesAndTodos = {
    state: () => ({
       notes: [
           {id: 1, title: 'Products Categories', todos: 'todos'},
           {id: 2, title: 'Burger Menu', todos: 'todos'}
       ],
        dialogVisibleNoteForm: false
    }),
    mutations: {
        setNotes(state, notes){
            state.notes = notes
        },
        setDialogVisibleNoteForm(state, dialogVisibleNoteForm){
            state.dialogVisibleNoteForm = dialogVisibleNoteForm
        }
    },
    actions: {
        openCreateNoteForm({state, commit}){
            commit('setDialogVisible', true)
        }
    },
    namespaced: true
}
